      <style>
         img.wp-smiley,
         img.emoji {
         display: inline !important;
         border: none !important;
         box-shadow: none !important;
         height: 1em !important;
         width: 1em !important;
         margin: 0 0.07em !important;
         vertical-align: -0.1em !important;
         background: none !important;
         padding: 0 !important;
         }

         .owl-stage-outer {
            padding: 30px 0;
         }

         .owl-nav {
            display: none;
         }

         .owl-dots {
         text-align: center;
         }

         .owl-dots span {
         position: relative;
         height: 10px;
         width: 10px;
         border-radius: 50%;
         display: inline-block;
         background: #fff;
         border: 2px solid #01b0f8;
         margin: 0 5px;
         }

         .owl-dots .active span {
         background: #01b0f8;
         box-shadow: none;
         height: 12px;
         width: 12px;
         /* margin-bottom: -1px; */
         }

         .card {
            background: #fff;
            box-shadow: 0 8px 30px -7px #c9dff0;
            margin: 0 20px;
            padding: 0 10px;
            border-radius: 20px;
            border: 0;

            .card-img-top {
            max-width: 100px;
            border-radius: 50%;
            margin: 15px auto 0;
            box-shadow: 0 8px 20px -4px #95abbb;
            width: 100px;
            height: 100px;
            }

            h5 {
            color: #01b0f8;
            font-size: 21px;
            line-height: 1.3;

            span {
                  font-size: 18px;
                  color: #666666;
            }
            }

            p {
            font-size: 18px;
            color: #555;
            padding-bottom: 15px;
            }
         }

         .active {
            opacity: 0.5;
            transition: all 0.3s;
         }

         .center {
            opacity: 1;

            h5 {
            font-size: 24px;

            span {
                  font-size: 20px;
            }
            }

            .card-img-top {
            max-width: 100%;
            height: 120px;
            width: 120px;
            }
         }
         }

         @media (max-width: 767px) {
         .gtco-testimonials {
            margin-top: 20px;
         }
         }

         .owl-carousel {
         .owl-nav button {
            &.owl-next, &.owl-prev {
            outline: 0;
            }
         }

         button.owl-dot {
            outline: 0;
         }
         }
      </style>
